import base64
import logging
import threading
import PySimpleGUI as sg
from main import main, stop
from version import __version__

#region Arguments related
class Args():
    main_folder = ""
    included_folders = ""
    target = ""
#endregion

#region Logging related
class Handler(logging.StreamHandler):
    def __init__(self):
        logging.StreamHandler.__init__(self)

    def emit(self, record):
        global buffer
        buffer = f"{buffer}\n{record.message}"
        window["-OUTPUT-"].update(value=buffer)

buffer = ""
logger = Handler()
logger.setLevel(logging.INFO)
logging.getLogger("").addHandler(logger)
#endregion

# Set the theme
sg.theme("LightGrey1")

# Set the layout
layout =    [
                [ sg.Text("Location of the files to sign") ],
                [ sg.InputText(size=(50, 1), key="-FILENAME-", expand_x=True), sg.FolderBrowse(key="-BROWSE-"), sg.Button("Sign", size=(10), button_color="Green"), sg.Button("Stop", size=(10), button_color="Red", visible=False) ],
                [ sg.Frame("Logs",
                    [
                        [
                            sg.Multiline(key="-OUTPUT-", size=(90, 25), echo_stdout_stderr=True, reroute_stdout=True, reroute_stderr=True, reroute_cprint = True, autoscroll=True, background_color="Black", text_color="White", disabled=True, expand_x=True, expand_y=True)
                        ]
                    ], expand_x=True, expand_y=True)
                ]
            ]

# Create the window
icon = b""
window = sg.Window(f"Sign me Senpai v{__version__}", icon=icon, layout=layout, resizable=True)

#region Functions
def the_main_thread():
    # Create the arguments for signing
    args = Args()
    args.main_folder = values["-FILENAME-"]
    # Disable the elements
    disable_elements(True)

    # Do the main thing
    try:
        main(args)
    except:
        pass

    # Enable the elements
    disable_elements(False)

def disable_elements(status):
    window["-FILENAME-"].update(disabled=status)
    window["-BROWSE-"].update(disabled=status)
    window["Sign"].update(disabled=status)
    window["Sign"].update(visible=not status)
    window["Stop"].update(disabled=not status)
    window["Stop"].update(visible=status)
#endregion

while True:
    event, values = window.read()
    if event in (sg.WIN_CLOSED, "Cancel"):
        break
    if event == "Sign":
        # Clear the output
        window["-OUTPUT-"].update(value="")
        # Start the main thread
        thread = threading.Thread(target=the_main_thread, daemon=True)
        thread.start()
    if event == "Stop":
        # Disable stop button while it's still stopping the main thread
        window["Stop"].update(disabled=True)
        # Stop the main thread
        stop()

window.close()